package controllers

import play.api._
import play.api.mvc._
import org.apache.commons.codec.binary.Base64
import org.apache.commons.io.IOUtils
import java.io.FileOutputStream
import java.io.File
import play.api.libs.json._
//

object Application extends Controller {
  
  def index = Action {
    Ok(views.html.index("Your new application is ready."))
  }
  
  // 10 MB size limit for now
  def capture = Action(parse.urlFormEncoded(maxLength = 1024 * 1024)) { request =>
	val body: Map[String, Seq[String]] = request.body
	val dataBody: Option[Seq[String]] = body.get("data") 

	// Expecting data body

	dataBody.map { datas =>
		//println("Testing to see if data :: data = " + data)
		//for (data <- datas) println("Akshay == ******** " + data + " *********\n\n")
		val jsonString: String = datas(0)

		//val jsonString = "{  \"users\":[    {      \"name\": \"Bob\",      \"age\": 31.0,      \"email\": \"bob@gmail.com\"    },    {      \"name\": \"Kiki\",      \"age\":  25.0,      \"email\": null    }  ]}"
		//val data: String = "Hello John"
		val json: JsValue = Json.parse(jsonString)

		//println("Testing to see if data :: json = \n" + json)

		val issue: Option[String] = (json \\ "Issue")(0).asOpt[String]

		//println("Testing to see if data :: issue = \n" + issue)

		//val imageData: Option[String] = (json \\ "data")(0).asOpt[String]
		//val imageData = "data:image/png;base64,"
//		val issue: Option[String] = (json \ "users").asOpt[String]
//		val imageData: Option[String] = (json \ "users" \ "name").asOpt[String]
		val imageData: String = jsonString.substring(jsonString.indexOf("data:image/png;base64,") + "data:image/png;base64,".length(), jsonString.lastIndexOf("\"]"))

		//val imageData

		println(issue.get + "\n\n")
		println("image data = \n\n" + imageData)

		val base64: Base64 = new Base64
		val imageDir = Play.current.configuration.getString("image.store.dir").get
		//val file = new FileWriter("/Users/srivatsasharma/Downloads/out.png")
		val file = new FileOutputStream(new File(imageDir + "out.png"));
		val decoded = base64 decode(imageData)
		
		println("decoded image data = \n\n" + decoded)

		IOUtils.write(decoded, file);
		//file.write(decoded)

		Ok("200")
	}.getOrElse {
		println("data parameter not sent in the request body")
    	BadRequest("Expecting urlFormEncoded data request body")  
  	}

  	
  }
  
}